import React from 'react';
import useScrollAnimation from '../hooks/useScrollAnimation';
import './Projects.css';

const Projects = () => {
  const [titleRef, titleVisible] = useScrollAnimation();
  const [gridRef, gridVisible] = useScrollAnimation();

  const projects = [
    {
      title: "E-Commerce Platform",
      description: "A full-stack e-commerce solution with React, Node.js, and MongoDB. Features include user authentication, payment integration, and admin dashboard.",
      technologies: ["React", "Node.js", "MongoDB", "Stripe"],
      image: "https://via.placeholder.com/400x200/667eea/ffffff?text=E-Commerce+Platform",
      githubLink: "https://github.com/yourusername/ecommerce-platform",
      liveLink: "https://your-ecommerce-app.vercel.app"
    },
    {
      title: "Task Management App",
      description: "A collaborative task management application with real-time updates, drag-and-drop functionality, and team collaboration features.",
      technologies: ["React", "Socket.io", "Express", "PostgreSQL"],
      image: "https://via.placeholder.com/400x200/48bb78/ffffff?text=Task+Manager",
      githubLink: "https://github.com/yourusername/task-manager",
      liveLink: "https://task-manager-demo.vercel.app"
    },
    {
      title: "Weather Dashboard",
      description: "A responsive weather application that provides current weather conditions and forecasts using external APIs with beautiful data visualization.",
      technologies: ["React", "Chart.js", "OpenWeather API", "CSS3"],
      image: "https://via.placeholder.com/400x200/4299e1/ffffff?text=Weather+Dashboard",
      githubLink: "https://github.com/yourusername/weather-dashboard",
      liveLink: "https://weather-dashboard-demo.vercel.app"
    },
    {
      title: "Portfolio Website",
      description: "A responsive portfolio website built with React, featuring smooth animations, modern design, and optimized performance.",
      technologies: ["React", "CSS3", "JavaScript", "Responsive Design"],
      image: "https://via.placeholder.com/400x200/ed8936/ffffff?text=Portfolio+Site",
      githubLink: "https://github.com/yourusername/portfolio",
      liveLink: "https://yourusername.github.io"
    }
  ];

  return (
    <section id="projects" className="projects section">
      <div className="container">
        <h2
          ref={titleRef}
          className={`section-title ${titleVisible ? 'animate-in' : ''}`}
        >
          Featured Projects
        </h2>
        <div
          ref={gridRef}
          className={`projects-grid ${gridVisible ? 'animate-in' : ''}`}
        >
          {projects.map((project, index) => (
            <div key={index} className="project-card">
              <div className="project-image">
                <img src={project.image} alt={project.title} className="project-img" />
              </div>
              <div className="project-content">
                <h3 className="project-title">{project.title}</h3>
                <p className="project-description">{project.description}</p>
                <div className="project-technologies">
                  {project.technologies.map((tech, techIndex) => (
                    <span key={techIndex} className="tech-tag">{tech}</span>
                  ))}
                </div>
                <div className="project-links">
                  <a href={project.githubLink} className="project-link">
                    GitHub
                  </a>
                  <a href={project.liveLink} className="project-link">
                    Live Demo
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Projects;
